-- Generated from Ops_Production_Log.xlsx (Production Log) and Ops_Shipping_Log.xlsx (Shipping Log)
-- Target: PostgreSQL 12+

BEGIN;

CREATE SCHEMA IF NOT EXISTS ops;

-- Staging/raw tables (one row per spreadsheet row).
CREATE TABLE IF NOT EXISTS ops.production_logs (
  production_log_id BIGSERIAL PRIMARY KEY,
  production_date   DATE,
  shift             TEXT,
  production_line   TEXT,
  raw_lot_id        TEXT,
  normalized_lot_id TEXT,
  part_number       TEXT,
  units_planned     INTEGER CHECK (units_planned >= 0),
  units_actual      INTEGER CHECK (units_actual >= 0),
  downtime_min      INTEGER CHECK (downtime_min >= 0),
  line_issue        BOOLEAN NOT NULL DEFAULT FALSE,
  primary_issue     TEXT,
  supervisor_notes  TEXT
);

CREATE TABLE IF NOT EXISTS ops.shipping_logs (
  shipping_log_id   BIGSERIAL PRIMARY KEY,
  ship_date         DATE,
  raw_lot_id        TEXT,
  normalized_lot_id TEXT,
  sales_order       TEXT,
  customer          TEXT,
  destination_state TEXT,
  carrier           TEXT,
  bol               TEXT,
  tracking_pro      TEXT,
  qty_shipped       INTEGER CHECK (qty_shipped >= 0),
  ship_status       TEXT,
  hold_reason       TEXT,
  shipping_notes    TEXT
);

-- Helpful indexes for weekly summary + drill-down.
CREATE INDEX IF NOT EXISTS idx_production_logs_week_line
  ON ops.production_logs (date_trunc('week', production_date), production_line);

CREATE INDEX IF NOT EXISTS idx_production_logs_norm_lot
  ON ops.production_logs (normalized_lot_id);

CREATE INDEX IF NOT EXISTS idx_shipping_logs_norm_lot
  ON ops.shipping_logs (normalized_lot_id);

CREATE INDEX IF NOT EXISTS idx_shipping_logs_ship_date
  ON ops.shipping_logs (ship_date);

-- Load data



COMMIT;
